<!--      <a [routerLink]="item.route">{{ item.name }}</a>-->
<!--    </li>-->
<!--  </ul>-->
<!--</div>-->

<div class="sidebar-slider" [class.sidebar-slide-in]="sidebarShow">
  <div
    class="sidebar-close"
    (click)="sidebarShow = !sidebarShow">
    X
  </div>
  <div class="sidebar-content">
    <h2>Sidebar</h2>
    <ul *ngIf="showSidebar">
      <li *ngFor="let item of items">
        <a [routerLink]="item.route">{{ item.name }}</a>
      </li>
    </ul>
  </div>
</div>
