import {AfterViewInit, Component, ViewChild} from '@angular/core';
import {SideBarComponent} from "../../components/side-bar/side-bar.component";

@Component({
  selector: 'app-layout',
  templateUrl: './layout.component.html',
  styleUrls: ['./layout.component.scss']
})
export class LayoutComponent implements AfterViewInit {
  @ViewChild(SideBarComponent)
  viewChild: SideBarComponent | any
  public sidebarStatus: boolean | any;

  constructor() {}

  ngAfterViewInit() {
    console.log(this.viewChild)
  }

  dataChangeHandler(data: any) {
    console.log(data)
    this.sidebarStatus = data;
    console.log('sidebar status', this.sidebarStatus)
  }

    sidebarItems = [
    {name: 'My Wallet', route: '/my_wallet'},
    {name: 'My Card', route: '/my_card'},
    {name: 'Finance Chart', route: '/finance_chart'},
    {name: 'Recent Transactions', route: '/recent_transactions'}
  ];
}
