import {Component, ViewChild} from '@angular/core';
import {SideBarComponent} from "../../components/side-bar/side-bar.component";

@Component({
  selector: 'app-layout',
  templateUrl: './layout.component.html',
  styleUrls: ['./layout.component.scss']
})
export class LayoutComponent {
  @ViewChild(SideBarComponent)
  viewChild: SideBarComponent | undefined;
  public sidebarStatus: boolean = false;

  dataSideBarHandler(data: boolean) {
    this.sidebarStatus = data;
  }

  sidebarItems = [
    {name: 'My Wallet', route: '/my_wallet'},
    {name: 'My Card', route: '/my_card'},
    {name: 'Finance Chart', route: '/finance_chart'},
    {name: 'Recent Transactions', route: '/recent_transactions'}
  ];
}
<div class="container">
  <div class="main-content">
    <div [ngClass]="{sidebarClose: sidebarStatus}" class="sidebar">
      <app-side-bar (dataSideBarChanged)="dataSideBarHandler($event)" [items]="sidebarItems"></app-side-bar>
    </div>
    <div class="main-content-area">
      <header>
        <app-nav-bar></app-nav-bar>
      </header>
      <router-outlet></router-outlet>
    </div>
  </div>
</div>
