  <div class="main-content">
    {{ value }}
    <div (change)="this.setValue($event)" [ngClass]="{ sidebarClose: sidebarStatus}" class="sidebar">
      <app-side-bar [items]="sidebarItems"></app-side-bar>
    </div>
    <div class="main-content-area">
      <header>
        <app-nav-bar></app-nav-bar>
