import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {DataService} from "../../services/data.service";
import {filter, interval, map, of, scan, take} from 'rxjs';

@Component({
  selector: 'app-side-bar',
  templateUrl: './side-bar.component.html',
  styleUrls: ['./side-bar.component.scss']
})
export class SideBarComponent implements OnInit {
  @Input() items: any[] | undefined;
  @Output() dataSideBarChanged: EventEmitter<any> = new EventEmitter<any>()
  public sidebarStatus: boolean = true;
  unreadTransactions: number | any = [];
  unreadTransactionsCount: number = 0;


  constructor(private dataService: DataService) {
    this.unreadTransactions = this.dataService.unreadTransactions
  }

  ngOnInit() {
    this.getUnreadTransactions(this.unreadTransactions)
  }

  changeStatusSideBar() {
    this.sidebarStatus = !this.sidebarStatus
    this.dataSideBarChanged.emit(!this.sidebarStatus)
  }


  getUnreadTransactions(data: any) {
    interval(500)
      .pipe(
        take(data.length),
        filter(res => data[res].type === 'no paid'),
        map(res => data[res].type),
        scan((acc, res) => acc.concat(res), [])
      )
      .subscribe((res) => {
        this.unreadTransactionsCount = res.length
      }, null)
  }
}
<div class="sidebar-slider" [class.sidebar-slide-in]="sidebarStatus">
  <div class="sidebar-content">
    <div class="sidebar-head">
      <div class="sidebar-head__logo">
        <img src="assets/images/union-logo.png" alt="Union Logo">
        <h2>Ringku</h2>
      </div>
      <div class="sidebar-head__toggle">
        <input
          type="checkbox"
          class="sidebar-toggle-button"
          id="sidebar-toggle-button"
          (click)="changeStatusSideBar()">
        <label id="burger" for="sidebar-toggle-button">
          <div></div>
          <div></div>
          <div></div>
        </label>
      </div>
    </div>
    <div class="wrapper-sidebar-main-content">
      <div class="sidebar-main">
        <ul>
          <li *ngFor="let item of items">
            <img src="assets/images/message.png" alt="Icon menu item">
            <a [routerLink]="item.route">{{ item.name }}</a>
            <span class="sidebar-main__unread-counter" *ngIf="item.unread && unreadTransactionsCount > 0">{{unreadTransactionsCount}}</span>
          </li>
        </ul>
      </div>
      <div class="sidebar-footer">
        <div class="sidebar-footer__setting-block">
          <img src="assets/images/setting.png" alt="Setting">
          <a href="#">Setting</a>
        </div>
        <hr [ngClass]="{'disable-divider': !sidebarStatus}" class="divider">
        <div class="sidebar-footer__user-block">
          <img src="assets/images/avatar.png" alt="Avatar">
          <span>Alex Grishe</span>
        </div>
      </div>
    </div>
  </div>
</div>
